import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { Abonnement, TypeAbonnement, TypeAbonnementValues } from 'src/app/models/abonnement';
import { AbonnementService } from 'src/app/services/abonnement.service';

@Component({
  selector: 'app-update-abonnement.ts',
  templateUrl: './update-abonnement.component.html',
  styleUrls: ['./update-abonnement.component.scss']
})
export class UpdateAbonnementComponent {
  abonnementForm: FormGroup;
  typeAbonnementValues = TypeAbonnementValues;
  abonnement : Abonnement;
  id : string;
  constructor(private formBuilder :FormBuilder , private abonnementService : AbonnementService,
              private router : Router,private activatedRoute :ActivatedRoute) {
                this.abonnementForm = this.formBuilder.group({
                  dateDebut: ["", [Validators.required]],
                  dateFin: ["", [Validators.required]],
                  prixAbon: ["", [Validators.required]],
                  typeAbonnement: [TypeAbonnement.ANNUEL, [Validators.required]]
    })
  }

  ngOnInit() {

    this.activatedRoute.paramMap.subscribe(res  => this.id = res.get('id'));

    this.abonnementService.getAbonnement(this.id).subscribe(
      (abonnementData: Abonnement) => {
        this.abonnement = abonnementData;
        // Patch the form with the Skieur data
        this.abonnementForm.patchValue({
          dateDebut: abonnementData.dateDebut,
          dateFin: abonnementData.dateFin,
          prixAbon: abonnementData.prixAbon,
          typeAbonnement: abonnementData.typeAbonnement,
        });
      },
      (error) => {
        console.error('Error fetching abonnement data:', error);
      }
    );


  }
  updateSkieur() {

    if (this.abonnementForm.valid) {
      let a : Abonnement = this.abonnementForm.value;
      this.abonnementService.updateAbonnement(a).subscribe(
        (response) => {
          if (response) {

            this.router.navigate(['abonnements']);
          } else {

            console.error('Unexpected response status:');
          }
        },
        (error) => {

          console.error('Error adding abonnement:', error);
        }
      )
    }


  }
  get numAbon(){
    return this.abonnementForm.get('numAbon');
  }
  get dateDebut(){
    return this.abonnementForm.get('dateDebut');
  }
  get dateFin(){
    return this.abonnementForm.get('dateFin');
  }
  get prixAbon(){
    return this.abonnementForm.get('prixAbon');
  } 
  get typeAbonnement(){
    return this.abonnementForm.get('typeAbonnement');
  }

}

<div class="p-4 sm:p-8 bg-white shadow sm:rounded-lg max-w-5xl mx-auto sm:px-6 lg:px-8 space-y-6">
  <form [formGroup]="abonnementForm" (submit)="updateAbonnement()">
    <div class="border-b border-gray-900/10 pb-12">
      <h2 class="text-base font-semibold leading-7 text-gray-900">Modifier une abonnement :</h2>

      <div class="mt-8 grid grid-cols-1  gap-x-6 gap-y-8 sm:grid-cols-6 items-start">
        <div class="sm:col-span-3">
          <label for="dateFin" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Date Debut</label>
          <input type="date" id="dateFin" formControlName="dateDebut" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 ">
          <div *ngIf="dateDebut.invalid && (dateDebut.dirty || dateDebut.touched) " class="text-sm text-red-600 space-y-1 mt-2">
            <div *ngIf="dateDebut.errors['required']">La date est obligatoire</div>
            <!-- Add more conditions for other errors -->
          </div>
        </div>
        <div class="sm:col-span-3">
          <label for="prenom" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Prenom</label>
          <input type="text" id="prenom" formControlName="prenomS" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5  ">
          <div *ngIf="prenomS.invalid && (prenomS.dirty || prenomS.touched)" class="text-sm text-red-600 space-y-1 mt-2">
            <div *ngIf="prenomS.errors['required']">Prenom est obligatoire</div>
            <!-- Add more conditions for other errors -->
          </div>
        </div>

        <div class="sm:col-span-3">
          <label for="ville" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Ville</label>
          <input type="text" id="ville" formControlName="ville" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5  ">
          <div *ngIf="ville.invalid && (ville.dirty || ville.touched)" class="text-sm text-red-600 space-y-1 mt-2">
            <div *ngIf="ville.errors['required']">ville est obligatoire</div>
            <!-- Add more conditions for other errors -->
          </div>
        </div>
        <div class="sm:col-span-3">
          <label for="dateN" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Date naissance</label>
          <input type="date" id="dateN" formControlName="dateNaissance" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full  p-2.5" placeholder="Select date">
          <div *ngIf="dateNaissance.invalid && (dateNaissance.dirty || dateNaissance.touched)" class="text-sm text-red-600 space-y-1 mt-2">
            <div *ngIf="dateNaissance.errors['required']">Date est obligatoire</div>
            <!-- Add more conditions for other errors -->
          </div>
        </div>

      </div>
    </div>


    <div class="mt-6 flex items-center justify-end gap-x-6">
      <button type="submit"  class="rounded-md bg-blue-700 cur uppercase px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-blue-900 cursor-pointer">
        Modifier</button>
    </div>
  </form>
</div>
