import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { CoursService } from "../../../services/cours.service";
import { Cours } from "../../../models/cours";
import { Router } from "@angular/router";

@Component({
  selector: 'app-add-cours',
  templateUrl: './add-cours.component.html',
  styleUrls: ['./add-cours.component.scss']
})
export class AddCoursComponent implements OnInit {

  coursForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private coursService: CoursService,
    private router: Router
  ) { }

  ngOnInit() {
    this.coursForm = this.formBuilder.group({
      niveau: ["", [Validators.required]],
      typeCours: ["", [Validators.required]],
      support: ["", [Validators.required]],
      prix: ["", [Validators.required]],
      creneau: ["", [Validators.required]]
    });
  }

  addCours() {
    if (this.coursForm.valid) {
      let cours: Cours = this.coursForm.value;
      this.coursService.addCours(cours).subscribe(
        (response) => {
          if (response) {
            this.router.navigate(['cours']);
          } else {
            console.error('Unexpected response status:');
          }
        },
        (error) => {
          console.error('Error adding cours:', error);
        }
      );
    }
  }

  get niveau() {
    return this.coursForm.get('niveau');
  }

  get typeCours() {
    return this.coursForm.get('typeCours');
  }

  get support() {
    return this.coursForm.get('support');
  }

  get prix() {
    return this.coursForm.get('prix');
  }

  get creneau() {
    return this.coursForm.get('creneau');
  }
}
<div class="p-4 sm:p-8 bg-white shadow sm:rounded-lg max-w-5xl mx-auto sm:px-6 lg:px-8 space-y-6">
  <form [formGroup]="coursForm" (submit)="addCours()">
    <div class="border-b border-gray-900/10 pb-12">
      <h2 class="text-base font-semibold leading-7 text-gray-900">Ajouter un cours :</h2>

      <div class="mt-8 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6 items-start">
        <div class="sm:col-span-3">
          <label for="niveau" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Niveau</label>
          <input type="text" id="niveau" formControlName="niveau" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5">
          <div *ngIf="niveau.invalid && (niveau.dirty || niveau.touched)" class="text-sm text-red-600 space-y-1 mt-2">
            <div *ngIf="niveau.errors['required']">Niveau est obligatoire</div>
          </div>
        </div>
        <div class="sm:col-span-3">
          <label for="typeCours" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Type de Cours</label>
          <input type="text" id="typeCours" formControlName="typeCours" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5">
          <div *ngIf="typeCours.invalid && (typeCours.dirty || typeCours.touched)" class="text-sm text-red-600 space-y-1 mt-2">
            <div *ngIf="typeCours.errors['required']">Type de cours est obligatoire</div>
          </div>
        </div>


      </div>
    </div>

    <div class="mt-6 flex items-center justify-end gap-x-6">
      <button type="submit" class="rounded-md bg-blue-700 cur uppercase px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-blue-900 cursor-pointer">
        Ajouter</button>
    </div>
  </form>
</div>
